    private void SalvarCadastroAnimalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SalvarCadastroAnimalActionPerformed
        Animal a = new Animal();
        
        a.setId_Animal(Integer.parseInt(IDCadastroAnimal.getText()));
        /*a.setNascimento(Nasc_CadastroAnimal.getText()); */
        String tipo_a = Combo_TipoAnimal.getSelectedItem()+"";
        a.setTipo(tipo_a);
        a.setRaca(RacaCadastroAnimal.getText());
        a.setNome(NomeCadastroAnimal.getText());
        
        
        
        /*String id_a = IDCadastroAnimal.getText();
        int id_a_int = Integer.parseInt(id_a);
        a.setId_Animal(id_a_int);
        String tipo_a = Combo_TipoAnimal.getSelectedItem()+"";
        a.setTipo(tipo_a); */
        
        if ((IDCadastroAnimal.getText().isEmpty())
                || (Combo_TipoAnimal.getSelectedIndex() == 0 )) {
        
        JOptionPane.showMessageDialog(null, "Todos os Campos precisam ser preenchidos");
        }
        else{
            ControllerAnimal ca = new ControllerAnimal();
            try{
                System.out.println("Tentando Gravar no BD");
                
                ca.salvar(a.getId_Animal(), a.getNascimento(), a.getTipo(), a.getRaca(), a.getNome());
                System.out.println("Gravado no BD com Sucesso");
                
                JOptionPane.showMessageDialog(null, "Salvo com Sucesso!");
            } catch (SQLException e) {
                e.printStackTrace(); 
            }finally{
                System.out.println("Fim da Inser√ß√£o");
                IDCadastroAnimal.setText("");
                
            }
        }
    }//GEN-LAST:event_SalvarCadastroAnimalActionPerformed

